import './Chatroom.css';
import { useEffect, useState } from "react";

function Chatroom() {
    const query = new URLSearchParams(window.location.search);
    const chatroom_code = query.get('code')

    const [messages, setMessages] = useState([]);

    function GetMessages() {
        const requestOptions = {
            method: 'GET',
            headers: { 
                'Content-Type': 'application/json',
                'Authorization': 'Token ' + localStorage.getItem('token')
            },
        };
        fetch(`http://127.0.0.1:8000/api/message/?chatroom_code=${chatroom_code}`, requestOptions)
            .then(response => response.json())
            .then((response) => {
                setMessages(response);
            });
    }

    useEffect(() => {GetMessages()});

    const rendermessage = () => {
        let messagelist = [];
        messages.forEach(message => {
            messagelist.push(<div class='message'><p><b>{message.sender}: </b> {message.message}</p></div>)
        });
        return messagelist;
    }

    function sendMessage(data) {
        const requestOptions = {
            method: 'POST',
            headers: { 
                'Content-Type': 'application/json',
                'Authorization': 'Token ' + localStorage.getItem('token')
            },
            body: JSON.stringify(
                {
                    "chatroom_code": chatroom_code,
                    "message": "testmessage2",
                    "user": "3aba250eac7ae6975e504cb47c252b09f6829c80"
                }
            )
        };
        fetch('http://127.0.0.1:8000/api/message/', requestOptions)
            .then(response => response.json())
            .then((data) => {
                if (data.message) {
                    console.log(data.message, ': ', data.token);
                    localStorage.setItem('token', data.token);
                    navigate("../", { replace: true });
                }
                else {
                    setError(data.error);
                    console.log(data.error);
                }
            });
    }

    return (
        <div class='body'>
            <h2>{chatroom_code}</h2>
            <div class='chatbox'>
                {rendermessage()}
                <form onSubmit={handleSubmit(onSubmit)}>
                    <input {...register('message', {
                        required: true
                    })}></input>
                    <center><input type='submit' value='Send'></input></center>
                </form>
            </div>
        </div>
    )
}

export default Chatroom;
