import './Home.css';
import { useForm} from 'react-hook-form';
import { Link, useNavigate } from 'react-router-dom';
import { useState } from "react";

function Home() {
    const {register, handleSubmit, formState: {errors}} = useForm();
    const [error, setError] = useState("");
    const [chatrooms, setChatrooms] = useState([]);

    const requestOptions = {
        method: 'GET',
        headers: { 
            'Content-Type': 'application/json',
            'Authorization': localStorage.getItem('token')
        },
    };
    fetch('http://127.0.0.1:8000/api/chatroom/', requestOptions)
        .then(response => response.json())
        .then((data) => {
            setChatrooms(data)
        });

    const navigate = useNavigate();

    const onSubmit = (data) => {
        const requestOptions = {
            method: 'GET',
            headers: { 
                'Content-Type': 'application/json',
                'Authorization': localStorage.getItem('token')

            },
            body: JSON.stringify(
                {
                    "chatroom_code": data.code
                }
            )
        };
        fetch('http://127.0.0.1:8000/api/chatroom/', requestOptions)
            .then(response => response.json())
            .then((data) => {
                if (data.message) {
                    console.log(data.message, ': ', data.token);
                    localStorage.setItem('token', data.token);
                    navigate("../", { replace: true });
                }
                else {
                    setError(data.error);
                    console.log(data.error);
                }
            });
    }

    return (
        <div class='body'>
            <div class='codeform'>
            <center><label>{error}</label></center>
                <form onSubmit={handleSubmit(onSubmit)}>
                    <h2>Enter Chatroom</h2>
                    <label>Code</label><input {...register('code', {
                        required: "Please enter the code of the chatroom"
                    })}></input>
                    <p>{errors.code?.message}</p>
                    <center><input type='submit' value='Enter'></input></center>
                </form>
            </div>
        </div>
    );
}

export default Home;
