import './Home.css';
import { useForm} from 'react-hook-form';
import { Link, useNavigate } from 'react-router-dom';
import { useEffect, useState } from "react";

function Home() {
    const {register, handleSubmit, formState: {errors}} = useForm();
    const [error, setError] = useState("");
    const [chatrooms, setChatrooms] = useState([]);

    const navigate = useNavigate();

    function GetChatroomData() {
        function Fetch() {
            const requestOptions = {
                method: 'GET',
                headers: { 
                    'Content-Type': 'application/json',
                    'Authorization': 'Token ' + localStorage.getItem('token')
                },
            };
            fetch('http://127.0.0.1:8000/api/chatroom/', requestOptions)
                .then(response => response.json())
                .then((data) => {
                    setChatrooms(data)
                    console.log(data);
            });
        }
        useEffect(() => {Fetch()}, []);

    }

    const onSubmit = (data) => {
        const requestOptions = {
            method: 'GET',
            headers: { 
                'Content-Type': 'application/json',
                'Authorization': 'Token ' + localStorage.getItem('token')
            },
        };
        fetch(`http://127.0.0.1:8000/api/message/?chatroom_code=${data.code}`, requestOptions)
            .then(response => response.json())
            .then((data) => {
                if (data.error) {
                    setError(data.error);
                    console.log(data.error);
                }
                else {
                    console.log(data);
                    return (
                        <p>data</p>
                    )
                }
            });
    }

    return (
        <div class='body'>
            <div class='codeform'>
            <center><label>{error}</label></center>
                <form onSubmit={handleSubmit(onSubmit)}>
                    <h2>Enter Chatroom</h2>
                    <label>Code</label><input {...register('code', {
                        required: "Please enter the code of the chatroom"
                    })}></input>
                    <p>{errors.code?.message}</p>
                    <center><input type='submit' value='Enter'></input></center>
                </form>
                <p>{chatrooms}</p>
            </div>
        </div>
    );
}

export default Home;
