import './Home.css';
import { useForm} from 'react-hook-form';
import { useNavigate } from 'react-router-dom';
import { useState } from "react";

function Home() {
    const {register, handleSubmit, formState: {errors}} = useForm();
    const [searcherror, setSearchError] = useState("");
    const [createerror, setCreateError] = useState("");

    const navigate = useNavigate();

    const searchSubmit = (data) => {
        const requestOptions = {
            method: 'GET',
            headers: { 
                'Content-Type': 'application/json',
                'Authorization': 'Token ' + localStorage.getItem('token')
            },
        };
        fetch(`http://127.0.0.1:8000/api/message/?chatroom_code=${data.search_code}`, requestOptions)
            .then(response => response.json())
            .then((request) => {
                if (request.error) {
                    setSearchError(request.error);
                    console.log(request.error);
                }
                else {
                    console.log(request);
                    navigate(`/chatroom/?code=${data.search_code}`, { replace: true });
                }
            }
        );
    }

    const createSubmit = (data) => {
        const requestOptions = {
            method: 'POST',
            headers: { 
                'Content-Type': 'application/json',
                'Authorization': 'Token ' + localStorage.getItem('token')
            },
            body: JSON.stringify(
                {
                    "code": data.create_code,
                    "user": localStorage.getItem('token')
                }
            )
        };
        fetch(`http://127.0.0.1:8000/api/chatroom/`, requestOptions)
            .then(response => response.json())
            .then((request) => {
                if (request.error) {
                    setCreateError(request.error);
                    console.log(request.error);
                }
                else {
                    console.log(request);
                    navigate(`/chatroom/?code=${data.code}`, { replace: true });
                }
            }
        );
    }

    return (
        <div class='body'>
            <div class='codeform'>
            <center><label>{searcherror}</label></center>
                <form onSubmit={handleSubmit(searchSubmit)}>
                    <h2>Enter Chatroom</h2>
                    <label>Code</label><input {...register('search_code', {
                        required: "Please enter the code of the chatroom"
                    })}></input>
                    <p>{errors.code?.message}</p>
                    <center><input type='submit' value='Enter'></input></center>
                </form>
            <center><label>{createerror}</label></center>
                <form onSubmit={handleSubmit(createSubmit)}>
                    <h2>Create Chatroom</h2>
                    <label>Code</label><input {...register('create_code', {
                        required: "Please enter the code of the chatroom"
                    })}></input>
                    <p>{errors.code?.message}</p>
                    <center><input type='submit' value='Create'></input></center>
                </form>
            </div>
        </div>
    );
}

export default Home;
